// Transform to dashboard expected format
const risks = [];

for (const item of $input.all()) {
  const data = item.json;

  // Parse contributing_factors if it's a string
  let factors = data.contributing_factors;
  if (typeof factors === 'string') {
    try { factors = JSON.parse(factors); } catch (e) { factors = {}; }
  }

  // Build summary from contributing factors
  const issues = [];
  if (factors.fault_signal > 0.5) issues.push('High fault frequency');
  if (factors.work_order_signal > 0.5) issues.push('Overdue maintenance');
  if (factors.notes_signal > 0.5) issues.push('Frequent crew concerns');
  if (factors.corrective_signal > 0.5) issues.push('Recurring repairs');

  const summary = issues.length > 0 
    ? issues.join(', ')
    : 'Multiple minor indicators detected';

  // Build contributing_factors array for dashboard
  const factorsList = [];
  if (factors.fault_count > 0) factorsList.push(`${factors.fault_count} faults in 90 days`);
  if (factors.overdue_count > 0) factorsList.push(`${factors.overdue_count} overdue tasks`);
  if (factors.note_count > 0) factorsList.push(`${factors.note_count} crew notes`);

  risks.push({
    equipment_id: data.equipment_id,
    equipment_name: data.equipment_name || 'Unknown Equipment',
    risk_score: parseFloat(data.risk_score) || 0,
    summary: summary,
    contributing_factors: factorsList.length > 0 ? factorsList : ['Risk score elevated']
  });
}

return [{ json: { risks } }];
